def add_assim_chamber(MaScene, coord0=array([0.,0.,0.])):
    diam = 1.5
    epais = 0.05
    Htot = 1.6
    bas = 0.2
    alpha = 3.14*55/180 #55 degre
    Lcone = diam/(2*cos(alpha))
    Hcone = Lcone*sin(alpha)

    ring = Cylinder(diam/2,epais,False,15)
    support = quadform(array([-epais/2.,0.,0.]), array([-epais/2.,0.,Htot]), array([epais/2.,0.,Htot]), array([epais/2.,0.,0.]))

    o1 = Shape(transformation (ring, 1,1,1,0,0,0,coord0[0],coord0[1],coord0[2]+bas), Material(Color3(250,250,250)))
    o2 = Shape(transformation (ring, 1,1,1,0,0,0,coord0[0],coord0[1],coord0[2]+Htot/3+bas), Material(Color3(250,250,250)))
    o3 = Shape(transformation (ring, 1,1,1,0,0,0,coord0[0],coord0[1],coord0[2]+2*Htot/3+bas), Material(Color3(250,250,250)))
    o4 = Shape(transformation (ring, 1,1,1,0,0,0,coord0[0],coord0[1],coord0[2]+Htot+bas), Material(Color3(250,250,250)))
    o5 = Shape(transformation (ring, 0.66,0.66,1,0,0,0,coord0[0],coord0[1],coord0[2]+Hcone/3+Htot+bas), Material(Color3(250,250,250)))
    o6 = Shape(transformation (ring, 0.33,0.33,1,0,0,0,coord0[0],coord0[1],coord0[2]+2*Hcone/3+Htot+bas), Material(Color3(250,250,250)))

    o7 = Shape(transformation (support, 1,2,1,0,0,0,coord0[0],coord0[1]+diam/2,coord0[2]+bas), Material(Color3(250,250,250)))
    o8 = Shape(transformation (support, 1,2,1,0,0,0,coord0[0],coord0[1]-diam/2.,coord0[2]+bas), Material(Color3(250,250,250)))
    o9 = Shape(transformation (support, 1,1,1,3.14/2,0,0,coord0[0]-diam/2,coord0[1],coord0[2]+bas), Material(Color3(250,250,250)))
    o10 = Shape(transformation (support, 1,1,1,3.14/2,0,0,coord0[0]+diam/2,coord0[1],coord0[2]+bas), Material(Color3(250,250,250)))

    o11 = Shape(transformation (support, 1,1,Lcone/Htot,0,0,3.14/2-alpha,coord0[0],coord0[1]+diam/2,coord0[2]+Htot+bas+epais), Material(Color3(250,250,250)))
    o12 = Shape(transformation (support, 1,1,Lcone/Htot,0,0,-(3.14/2-alpha),coord0[0],coord0[1]-diam/2,coord0[2]+Htot+bas+epais), Material(Color3(250,250,250)))
    o13 = Shape(transformation (support, 1,2,Lcone/Htot,3.14/2,0,-(3.14/2-alpha),coord0[0]+diam/2,coord0[1],coord0[2]+Htot+bas+epais), Material(Color3(250,250,250)))
    o14 = Shape(transformation (support, 1,2,Lcone/Htot,3.14/2,0,3.14/2-alpha,coord0[0]-diam/2,coord0[1],coord0[2]+Htot+bas+epais), Material(Color3(250,250,250)))

    ls = [o1, o2, o3, o4, o5, o6, o7, o8, o9, o10, o11, o12, o13, o14]
    for obj in ls:
        obj.setName('22000000000')#can id
        MaScene.add (obj)

    return MaScene

